@mixin pos-center {
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

@mixin pos-middle {
  top: 50%;
  transform: translateY(-50%);
}

@mixin banner-w {
  h3 {
    color: #fff;
    font-size: 40px;
    line-height: 1.5;
    font-weight: 500;
  }

  p {
    color: #fff;
    font-size: 28px;
    font-weight: 300;
    margin-top: 14px;
    line-height: 1.5;
  }
}

@mixin banner-b {
  h3 {
    font-weight: 500;
    width: 400px;
    line-height: 1.5;
    color: #000;
    font-size: 35px;
  }

  p {
    color: #000;
    font-size: 28px;
    font-weight: 300;
    margin-top: 14px;
    line-height: 1.5;
  }
}

@mixin contents-title {
  .title-box {
    margin-bottom: 20px;

    h2 {
      padding: 60px 0 5px 0;
      font-size: 16px;
      line-height: 1.5;
      color: $point-color;
    }

    p {
      font-size: 24px;
      line-height: 1.5;

    }

    em {
      font-size: 14px;
      line-height: 1.5;
      font-weight: 200;
    }

    .more-box {
      display: flex;
      justify-content: end;
      padding-top: 16px;

      a {
        font-size: 14px;
        line-height: 1.5;
        font-weight: 500;
        color: #a6a6a6;
      }

    }
  }
}

@mixin main-content-layout-1 {
    display: flex;
    flex-wrap: wrap;
    margin: 0 -10px;

    li {
      width: 250px;
      height: 282px;
      padding: 0 10px;

      box-sizing: border-box;

