    @include contents-title();
    .main-content{
      @include main-content-layout-1();
    }
  }
}

section.steady-sell {
  .container {
    @include contents-title();
    ul.main-content {
      display: flex;
      justify-content: space-between;
      flex-wrap: wrap;
      margin-top: 20px;
      &>li{
        width: calc((100% - 40px) / 3);
        overflow: hidden;
        img{
          width: 100%;
        }
        &:nth-child(n+4){
          margin-top: 20px;
        }
      }
    }
  }
}

section.best-sell{
  .container{
    @include contents-title();
    .main-content{
      @include main-content-layout-1();
    }
  }
}

section.learn-guide{
  @include contents-title();
  .main-content{
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    li{
      width: 230px;
      overflow: hidden;
      margin-top: 20px;
      img{
        width: 100%;
      }
      &:nth-child(n+5){
        margin-top: 20px;
      }
    }
  }
}

section.event{
  .container{
    overflow: hidden;
    padding: 0 19px;
    @include contents-title();
    position: relative;
    height: 100%;
    .mySwiper1 {
      width: 100%;
      height: 100%;
      overflow: visible;
      .swiper-slide {
        text-align: center;
        font-size: 18px;
        background: #fff;
      img {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }
      }
      .swiper-button-next,
      .swiper-button-prev{
        width: 35px;
        height: 35px;
        background-color: #fff;
        border-radius: 50%;
        box-shadow: 1px 1px 6px rgba(0, 0, 0, .5);
        &::after{
          color: $point-color;
          font-size: 15px;
        }
        &:hover{
          background-color: $point-color;
          &::after{
            color: #fff;
          }
        }
      }
      .swiper-button-prev{
        left: -15px;
      }
      .swiper-button-next{
        right: -15px;
      }
    }
  }
}
section.fast-article{
  margin-bottom: 250px;
  .container{
    @include contents-title();
    .main-content{
      display: flex;
      padding-bottom: 20px;
      li{
        width: calc((100% - 40px) / 3);
        margin-right: 20px;
        cursor: pointer;
        &:last-child{
          margin: 0;
        }
        img{
          width: 100%;
        }
        p{
          font-size: 13px;
          font-weight: 400;
          margin-top: 16px;
          padding-right: 14px;
          &>img{
            display: none;
            width: 12px;
            margin-left: 3px;
          }
        }

        &:hover p{
          text-decoration: underline;
          &>img{
            display: inline-block;
          }
        }
      }
    }  
    &>a{
      display: block;
      background-color: $point-color;
      width: 200px;
      height: 47px;
      text-align: center;
      line-height: 47px;
      font-weight: 300;
      font-style: 14px;
      color: #fff;
      margin: 0 auto;
    }
  }
}
section.category-button{
  .cate-box{
    width: 100%;
    h2{
      padding: {
        right: 20px;
        bottom: 25px;
      };
      background-color: #fff;
    }
  }
}
